function getFestivals(filters) {
    const url = `https://data.culture.gouv.fr/api/records/1.0/search/?${new URLSearchParams(filters)}`;

    return fetch(url)
        .then(response => {
            if (!response.ok) {
                throw new Error('Une erreur s\'est produite lors de la récupération des festivals');
            }
            return response.json();
        })
        .then(data => data.records)
        .catch(error => {
            _d71‍.g.console.error('Une erreur s\'est produite lors de la récupération des festivals :', error);
            throw error;
        });
}

